import React, { useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";
import "./App.css";
import Home from "./Home";
import SnackOrBoozeApi from "./Api";
import NavBar from "./NavBar";
import { Route, Switch } from "react-router-dom";
import Menu from "./FoodMenu";
import Snack from "./FoodItem";

function App() {
  const INITIAL_ROUTES = ["snacks", "drinks"]
  const [isLoading, setIsLoading] = useState(true);
  const [snac, setSnacks] = useState([]);
  const [drinks, setDrinks] = useState([]);

  useEffect(() => {
    async function getSnacks() {
      for (const route of INITIAL_ROUTES){
        let snack = await SnackOrBoozeApi.getSnacks(route);
        setSnacks(...{[route]: snack});
        setIsLoading(false);
      }
  }
  getSnacks();
}, []);
  console.log(snac)

  if (isLoading) {
    return <p>Loading &hellip;</p>;
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />
        <main>
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route exact path="/snacks">
              <Menu snacks={snac["snacks"]} title="Snacks" />
            </Route>
            <Route path="/snacks/:id">
              <Snack items={snac["snacks"]} cantFind="/snacks" />
            </Route>
            <Route>
              <p>Hmmm. I can't seem to find what you want.</p>
            </Route>
          </Switch>
        </main>
      </BrowserRouter>
    </div>
  );
}

export default App;
